int countLevel(Node* root, int level) {
    if (root == NULL)
        return 0;

    if (level == 0)
        return 1;      // ako smo stigli do zeljenog nivoa

    return countLevel(root->left, level - 1)
         + countLevel(root->right, level - 1);
}
